import React, { useState, useEffect } from 'react';

function ProductCard({ productName, productImage, productDescription, productLink }) {
  const [likes, setLikes] = useState(0);

  const handleLike = () => {
    setLikes(likes + 1);
  };

  return (
    <div style={styles.card}>
      <img src={productImage} alt={productName} style={styles.image} />
      <h2 style={styles.title}>{productName}</h2>
      <p style={styles.description}>{productDescription}</p>
      <a href={productLink} target="_blank" rel="noopener noreferrer" style={styles.link}>
        Visit API Repository
      </a>
      <div style={styles.footer}>
        <button onClick={handleLike} style={styles.likeButton}>
          ❤️ Like
        </button>
        <span style={styles.likesCount}>{likes} Likes</span>
      </div>
    </div>
  );
}




export default function App() {
  return (
    <div style={{ marginTop: '50px' }}>
      <h1 style={{ textAlign: 'center' }}>Product List</h1>
      <ProductList />
    </div>
  );
}
