import React, { useState, useEffect } from 'react';

function ProductCard({ productName, productImage, productDescription, productLink }) {
    const [likes, setLikes] = useState(0);

    const handleLike = () => {
        setLikes(likes + 1);
    };

    return (
        <div style={styles.card}>
            <img src={productImage} alt={productName} style={styles.image} />
            <h2 style={styles.title}>{productName}</h2>
            <p style={styles.description}>{productDescription}</p>
            <a href={productLink} target="_blank" rel="noopener noreferrer" style={styles.link}>
                Visit API Repository
            </a>
            <div style={styles.footer}>
                <button onClick={handleLike} style={styles.likeButton}>
                    ❤️ Like
                </button>
                <span style={styles.likesCount}>{likes} Likes</span>
            </div>
        </div>
    );
}

const styles = {
    card: {
        border: '1px solid #ddd',
        borderRadius: '8px',
        padding: '16px',
        maxWidth: '300px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    },
    image: {
        width: '100%',
        height: 'auto',
        borderRadius: '8px',
    },
    title: {
        fontSize: '1.5rem',
        margin: '8px 0',
    },
    description: {
        fontSize: '1rem',
        color: '#555',
    },
    link: {
        display: 'block',
        margin: '8px 0',
        color: '#007bff',
        textDecoration: 'none',
    },
    footer: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginTop: '16px',
    },
    likeButton: {
        backgroundColor: '#ff4d4d',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        padding: '8px 12px',
        cursor: 'pointer',
    },
    likesCount: {
        fontSize: '1rem',
        color: '#333',
    },
};

function ProductList() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
            .then((response) => response.json())
            .then((data) => setProducts(data))
            .catch((error) => console.error('Error fetching products:', error));
    }, []);

    return (
        <div style={styles.list}>
            {products.map((product) => (
                <ProductCard
                    key={product.id}
                    productName={product.title}
                    productImage={product.image}
                    productDescription={product.description}
                    productLink={product.id}
                />
            ))}
        </div>
    );
}


export default function App() {
    return (
        <div style={{ marginTop: '50px' }}>
            <h1 style={{ textAlign: 'center' }}>Product List</h1>
            <ProductList />
        </div>
    );
}
