import React, { useEffect } from 'react';
import { createStore } from 'redux';

// Create store
const store = createStore(apiReducer);
import { Provider, useDispatch, useSelector } from 'react-redux';

// Initial state
const initialState = {
    apis: [],
};

// Reducer
function apiReducer(state = initialState, action) {
    switch (action.type) {
        case 'SET_APIS':
            return { ...state, apis: action.payload };
        default:
            return state;
    }
}



function ProductCard({ productName, productImage, productDescription }) {
    const [likes, setLikes] = React.useState(0);

    const handleLike = () => {
        setLikes(likes + 1);
    };

    return (
        <div style={styles.card}>
            <img src={productImage} alt={productName} style={styles.image} />
            <h2 style={styles.title}>{productName}</h2>
            <p style={styles.description}>{productDescription}</p>
            <div style={styles.footer}>
                <button onClick={handleLike} style={styles.likeButton}>
                    ❤️ Like
                </button>
                <span style={styles.likesCount}>{likes} Likes</span>
            </div>
        </div>
    );
}

export default ProductCard;
