// Initial state
const initialState = {
    apis: [],
};

// Reducer
function apiReducer(state = initialState, action) {
    switch (action.type) {
        case 'SET_APIS':
            return { ...state, apis: action.payload };
        default:
            return state;
    }
}

// Create store
const store = redux.createStore(apiReducer);

function ProductCard({ productName, productImage, productDescription }) {
    const [likes, setLikes] = React.useState(0);

    const handleLike = () => {
        setLikes(likes + 1);
    };

    return (
        <div style={styles.card}>
            <img src={productImage} alt={productName} style={styles.image} />
            <h2 style={styles.title}>{productName}</h2>
            <p style={styles.description}>{productDescription}</p>
            <div style={styles.footer}>
                <button onClick={handleLike} style={styles.likeButton}>
                    ❤️ Like
                </button>
                <span style={styles.likesCount}>{likes} Likes</span>
            </div>
        </div>
    );
}

const styles = {
    card: {
        border: '1px solid #ddd',
        borderRadius: '8px',
        padding: '16px',
        maxWidth: '300px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    },
    image: {
        width: '100%',
        height: 'auto',
        borderRadius: '8px',
    },
    title: {
        fontSize: '1.5rem',
        margin: '8px 0',
    },
    description: {
        fontSize: '1rem',
        color: '#555',
    },
    footer: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginTop: '16px',
    },
    likeButton: {
        backgroundColor: '#ff4d4d',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        padding: '8px 12px',
        cursor: 'pointer',
    },
    likesCount: {
        fontSize: '1rem',
        color: '#333',
    },
};

function PublicAPIs() {
    const dispatch = useDispatch();
    const apis = useSelector((state) => state.apis);

    useEffect(() => {
        fetch('https://api.publicapis.org/entries')
            .then((response) => response.json())
            .then((data) => {
                dispatch({ type: 'SET_APIS', payload: data.entries });
            })
            .catch((error) => console.error('Error fetching APIs:', error));
    }, [dispatch]);

    return (
        <div>
            <h1>Public APIs</h1>
            <ul>
                {apis.map((api, index) => (
                    <li key={index}>
                        <strong>{api.API}</strong>: {api.Description}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default function App() {
    return (
        <Provider store={store}>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '50px' }}>
                <ProductCard
                    productName="Awesome Product"
                    productImage="https://via.placeholder.com/300"
                    productDescription="This is an amazing product you will love!"
                />
                <PublicAPIs />
            </div>
        </Provider>
    );
}
