import React, { useState, useEffect } from 'react';

function ProductCard({ productName, productImage, productDescription, productLink }) {
  const [likes, setLikes] = useState(0);

  function handleLike() {
    if (likes < 1) {
      setLikes(likes + 1);
    } else {

    }



  }

  return (
    <div style={styles.card}>
      <img src={productImage} alt={productName} style={styles.image} />
      <h2 style={styles.title}>{productName}</h2>
      <p style={styles.description}>{productDescription}</p>
      <div style={styles.footer}>
        <button onClick={handleLike} style={styles.likeButton}>
          ❤️
        </button>
      </div>
    </div>
  );
}

const styles = {
  card: {
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '16px',
    width: '300px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  image: {
    width: '100%',
    height: '400px',
    objectFit: 'cover',
    borderRadius: '8px',
    objectFit: 'contain',
  },
  title: {
    fontSize: '1.2rem',
    margin: '8px 0',
    textAlign: 'center',
  },
  description: {
    fontSize: '0.9rem',
    color: '#555',
    textAlign: 'center',
    overflow: 'hidden',
    display: '-webkit-box',
    WebkitBoxOrient: 'vertical',
    WebkitLineClamp: 3,
  },
  link: {
    display: 'block',
    margin: '8px 0',
    color: '#007bff',
    textDecoration: 'none',
  },
  footer: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginTop: '16px',
    width: '100%',
  },
  likeButton: {
    backgroundColor: '#ffff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    padding: '8px 12px',
    cursor: 'pointer',
  },
  list: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '16px',
    justifyContent: 'center',
    padding: '16px',
  },
};

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('https://fakestoreapi.com/products')
      .then((response) => response.json())
      .then((data) => setProducts(data))
      .catch((error) => console.error('Error fetching products:', error));
  }, []);

  return (
    <div style={styles.list}>
      {products.map((product) => (
        <ProductCard
          key={product.id}
          productName={product.title}
          productImage={product.image}
          productDescription={product.description}
          productLink={product.id}
        />
      ))}
    </div>
  );
}

export default function App() {
  return (
    <div style={{ marginTop: '50px' }}>
      <h1 style={{ textAlign: 'center' }}>Product List</h1>
      <ProductList />
    </div>
  );
}
